import React from "react";
import TodoHeader from '../header/todoHeader';
import TodoList from '../todoList/todoList';
import TodoForm from '../form/todoForm';



class TodoApp extends React.Component {
  constructor (props) {
    super(props);
    this.addItem = this.addItem.bind(this);
    this.removeItem = this.removeItem.bind(this);
    this.markTodoDone = this.markTodoDone.bind(this);
    this.state = {
      todoItems: []
    };
  }


  componentDidMount() {
    this.fetchData();
  }

  fetchData = async () => {
    try {
      const response = await fetch('https://jsonplaceholder.typicode.com/todos');
      const jsonData = await response.json();
      const firstTenTodos = jsonData.slice(0, 10);
      this.setState({ todoItems: firstTenTodos });
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  addItem(todoItem) {
    const newTodoItems = [
      ...this.state.todoItems,
      {
        index: this.state.todoItems.length + 1,
        value: todoItem.newItemValue,
        done: false
      }
    ];
    this.setState({todoItems: newTodoItems});
  }

  removeItem (itemIndex) {
    const updateTodoItem = this.state.todoItems.filter(todo => todo.id !== itemIndex)
    this.setState({todoItems: updateTodoItem});
  }

 todoCompleted(itemIndex) {
  const updateTodoItem = this.state.todoItems.map((todo,index) => {
    if (index === itemIndex) {
      return {
        ...todo,
        completed: !todo.completed,
      }
    }
    return todo;
  })
    this.setState({todoItems: updateTodoItem});  
  }
  render() {
    return (
      <div id="main">
        <TodoHeader />
        <TodoList items={this.props.initItems} removeItem={this.removeItem} markTodoDone={this.todoCompleted}/>
        <TodoForm addItem={this.addItem} />
      </div>
    );
  }
}

export default TodoApp;
