import React from 'react';
import './styles/index.css';
import TodoForm from './components/TodoForm';
import TodoList from './components/TodoList';


class App extends React.Component {
  constructor(props) {
    super(props);
    this.state ={
      todoItems: []
    }
  }

  componentDidMount() {
    this.fetchData();
  }

  fetchData = async () => {
    try {
      const response = await fetch('https://jsonplaceholder.typicode.com/todos');
      const jsonData = await response.json();
      // Slice the data array to get only the first 10 todos
      const firstTenTodos = jsonData.slice(0, 10);
      this.setState({ todoItems: firstTenTodos });
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  addItem(todoItem) {
    const newTodoItems = [
      ...this.state.todoItems,
      {
        index: this.state.todoItems.length + 1,
        value: todoItem.newItemValue,
        done: false
      }
    ];
    this.setState({ todoItems: newTodoItems });
  }

  removeItem(itemIndex) {
    // Avoid directly mutating state, use setState instead
    const updatedTodoItems = this.state.todoItems.filter(
      (todo, index) => index !== itemIndex
    );
    this.setState({ todoItems: updatedTodoItems });
  }

  TodoCompleted(itemIndex) {
    // Avoid directly mutating state, use setState instead
    const completedItems = this.state.todoItems.map((todo, index) => {
      if (index === itemIndex) {
        return {
          ...todo,
          completed: !todo.completed
        };
      }
      return todo;
    });
    this.setState({ todoItems: completedItems });
  }


 render() {
